import React from 'react';


export default function Pagination ({
  currentPage,
  prevPage,
  nextPage,
  selectPage,
}) {
  const pagesList = (() => {
    const list = [];

    let lastInList = 5;
    while (currentPage > lastInList) {
      lastInList += 5;
    }

    while (list.length < 5) {
      list.unshift(lastInList--);
    }

    return list;
  })();

  return (
    <div className="pagination-container">
      <div
        classNames="pages-list"
        onClick={prevPage}
      >
        {`<`}
      </div>
        {
          pagesList.map((page) => (
            <div
              classNames={page === currentPage ? "current-page" : "pages-list"}
              onClick={() => selectPage(page)}
            >
              {page}
            </div>
          ))
        }
      <div
        classNames="pages-list"
        onClick={nextPage}
      >
        {`>`}
      </div>
    </div>
  );
}
