import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Table from './Table';

import './App.css';


function App() {
  const [ state, setState ] = useState({
    sortBy: 'in_frame',
    order: 'asc',
    filterLocation: '',
    pageNumber: 1,
    pageSize: 10,
  });

  useEffect (() => {
    const {
      sortBy,
      order,
      filterLocation,
      pageNumber,
      pageSize,
    } = state;

    axios.get('/data', {
      params: {
        sortBy,
        order,
        filterLocation,
        pageNumber,
        pageSize,
      },
    })
      .then(({ data }) => {
        console.log(data);
        setState({ data });
      });
  }, []);

  const setSortBy = (sortBy) => {
    console.log('HERE');
    setState({
      ...state,
      sortBy,
      order: state.order === 'asc' ? 'desc' : 'asc', 
    });
  }

  return (
    <div className="App">
      <Table data={state.data} setSortBy={setSortBy} />
    </div>
  );
}

export default App;
